import axios from "axios"
import { useEffect, useState } from "react"
import { Navigate } from "react-router-dom"
const PokeLista = (props) => {
    const [pokemons, setPokemons] = useState([])

    useEffect(()=>{
        axios.get("https://pokeapi.co/api/v2/pokemon")
        .then(response =>{
            setPokemons(response.data.results);
        });
    },[]);

    const funcionNavegar = (p) => {
        //nagivate ("/pokemon/" + p)
        Navigate(`/pokeon/${p}`)
    }
    {pokemons.map(p =>{
    return <div>
        <button onClick={funcionNavegar}>Navegar</button>
        <link to="/pokeon/25">Ir a Pikachu</link>
        <h1>Lista</h1>
        <p onClick={() =>{Navigate(`/pokemon/${p.name}`)}}>
            Este pokemon es {p.name}
        </p>
    </div>
}
    )}
}
export default PokeLista
